        public UIControlledApplication thisUIcontrolApp { get; private set; }



        public Result OnStartup(UIControlledApplication application)
        {

            #region IHB folder

            IHB_Properties.IHBFolder = IHB_Methods.GetIHBFolder();

            #endregion IHB folder



            //TODO: Descomentar aqui
            //wndw_listaCabos_02 = null;   // no dialog needed yet; the command will bring it
            thisApp = this;  // static access to this application instance

            this.thisUIcontrolApp = application;
            IHB_Properties.uiControlledApplication = application;

           
            AddRibbonPanel(application);


            application.ControlledApplication.DocumentOpened
                    += new EventHandler<Autodesk.Revit.DB.Events.DocumentOpenedEventArgs>(Application_DocumentOpened);

            application.ControlledApplication.DocumentClosing
                += new EventHandler<Autodesk.Revit.DB.Events.DocumentClosingEventArgs>(Application_DocumentClosing);







            return Result.Succeeded;
        }






        private void Application_DocumentOpened(object sender, Autodesk.Revit.DB.Events.DocumentOpenedEventArgs args)
        {
            #region IHB folder

            IHB_Properties.IHBFolder = IHB_Methods.GetIHBFolder();

            #endregion IHB folder


            Document doc = args.Document;
            IHB_Properties.RevitUsername = doc.Application.Username;

        }


        private void Application_DocumentClosing(object sender, Autodesk.Revit.DB.Events.DocumentClosingEventArgs args)
        {

            Document doc = args.Document;

            IHB_Properties.uiControlledApplication.ControlledApplication.DocumentOpened -= Application_DocumentOpened;

            IHB_Properties.uiControlledApplication.ControlledApplication.DocumentOpened
                    += new EventHandler<Autodesk.Revit.DB.Events.DocumentOpenedEventArgs>(Application_DocumentOpened);

        }





