            application.ControlledApplication.DocumentClosing -= Application_DocumentClosing;

            return Result.Succeeded;
        }










        #region Ribbons, botões, painéis e etc

        /// <summary>
        /// Cria a aba, ribbons, botões e etc do plugin.
        /// </summary>
        /// <param name="application"></param>
        private void AddRibbonPanel(UIControlledApplication application)
        {
            // Create custom ribbon tab
            String tabName = "I Have BIM";
            application.CreateRibbonTab(tabName);


            #region Code Editor Panel

            //Panel
            RibbonPanel ribbonPanelDevTools = application.CreateRibbonPanel(tabName, "DEV Tools");

            //Buttons
            PushButtonData codeEditor_buttonData = new PushButtonData(
                "IHB_cmdCodeEditor",
                "Code\nEditor",
                thisAssemblyPath,
                "IHB_RevitApiDevTools.CodeEditor.Commands.CodeEditor");

            PushButton codeEditor_pushButton = ribbonPanelDevTools.AddItem(codeEditor_buttonData) as PushButton;
            codeEditor_pushButton.ToolTip = "C# code editor to run scripts directly in Revit.";

            string base64_CodeEditor = "";
            System.Drawing.Image imgCodeEditor = System.Drawing.Image.FromStream(new MemoryStream(Convert.FromBase64String(base64_CodeEditor)));
            codeEditor_pushButton.LargeImage = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgCodeEditor, 32, 32));
            codeEditor_pushButton.Image = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgCodeEditor, 16, 16));


            #endregion Code Editor Panel




            #region General Panel

            //Panel
            RibbonPanel ribbonPanelIhb = application.CreateRibbonPanel(tabName, "IHB");

            //Buttons
            PushButtonData help_pushButton = new PushButtonData(
                "IHB_cmdHelp",
                "Help",
                thisAssemblyPath,
                "IHB_RevitApiDevTools.Help.Commands.OpenHelp");

            help_pushButton.ToolTip = "IHB Revit API DEV Tools help";

            string base64_Help = "";
            System.Drawing.Image imgHelp = System.Drawing.Image.FromStream(new MemoryStream(Convert.FromBase64String(base64_Help)));
            help_pushButton.LargeImage = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgHelp, 24, 24));
            help_pushButton.Image = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgHelp, 16, 16));



            PushButtonData about_pushButton = new PushButtonData(
                "IHB_cmdAbout",
                "About",
                thisAssemblyPath,
                "IHB_RevitApiDevTools.About.Commands.OpenAbout");

            about_pushButton.ToolTip = "About IHB Revit API DEV Tools";

            string base64_About = "";
            System.Drawing.Image imgAbout = System.Drawing.Image.FromStream(new MemoryStream(Convert.FromBase64String(base64_About)));
            about_pushButton.LargeImage = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgAbout, 24, 24));
            about_pushButton.Image = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(imgAbout, 16, 16));
            
            IList<RibbonItem> stackedItems = ribbonPanelIhb.AddStackedItems(help_pushButton, about_pushButton);


            #endregion General Panel


        }



        private void AddPulldownButtonGroup(PulldownButton group, PushButtonData pBtnData, string btnBase64Img = null, string btnFolder = null, string btnImgName = null, string btnToolTip = "", bool enabled = true)
        {
            PushButton pBtn = group.AddPushButton(pBtnData) as PushButton;
            pBtn.ToolTip = btnToolTip;
            pBtn.Enabled = enabled;
            //pBtn.Image = BmpImageSource("RevitAddinCSProject.Resources.Embedded_Item1_16x16.bmp");
            //pBtn.LargeImage = BmpImageSource("RevitAddinCSProject.Resources.Embedded_Item1_32x32.bmp");

            if (btnBase64Img == null)
            {
                AddButtonImage(this.thisAssemblyPath, pBtn, btnFolder, btnImgName);
            }
            else
            {
                System.Drawing.Image img = System.Drawing.Image.FromStream(new MemoryStream(Convert.FromBase64String(btnBase64Img)));
                pBtn.LargeImage = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(img, 32, 32));
                pBtn.Image = IHB_Methods.BitmapToBitmapSource(IHB_Methods.ResizeImage(img, 16, 16));
            }

        }


        private void AddButtonImage(string assemblyPath, PushButton pushBtn, string path2, string path3)
        {
            Uri uriImage = new Uri(Path.Combine(Path.GetDirectoryName(assemblyPath), path2, path3));
            BitmapImage pbImage = new BitmapImage(uriImage);
            pushBtn.LargeImage = pbImage;
        }


